Codeages = [17,34,29,18,46,55,9,60,35,14,56,64] 
state = [] 
for i in ages: 
    if i &lt;18: 
        state.append("young") 
    elif i&gt;=18 and i&lt;60: 
        state.append("adult") 
    else: 
